package randomSiteJavaPrograms;

public class Simplenumberprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num ;
		for(int i =0; i<5;i++) {
			num=1;
			for(int j=0;j<=i;j++)
			{
				System.out.print(num+" ");
				num++;

			}System.out.println();
		}
	}

}
